
update ECOLE_DOCT set SOURCE_ID = (select id from source where CODE  = 'COMUE::SYGAL');

-- avancement de la sequence
DECLARE
  maxid NUMBER;
  nextval NUMBER;
BEGIN
  select max(id) into maxid from ECOLE_DOCT;
  loop
    select ECOLE_DOCT_ID_SEQ.nextval into nextval from dual;
    EXIT WHEN maxid < nextval;
  end loop;
END;
/



