  ) VALUES (
    389, 27656, q'|Ιnfοrmatiοns vibrοtactiles pοur l'aide à la navigatiοn et la gestiοn des cοntacts avec l'envirοnnement|', 'fr', v_resume, v_resume_a, q'|Perception ; dispositifs tactiles ; approche écologique ; interfaces homme-machine ; multimodalité ; navigation|', NULL, q'|Vibrotactile information for approach regulation and making contacts|', q'|Perception ; tactile displays ; ecological approach ; human-machine interfaces ; multimodality ; navigation|'
