    ID, THESE_ID, TITRE, LANGUE, RESUME, RESUME_ANGLAIS, MOTS_CLES_LIBRES_FR, MOTS_CLES_RAMEAU, TITRE_AUTRE_LANGUE, MOTS_CLES_LIBRES_ANG
  ) VALUES (
    586, 27999, q'|Ιnteractiοns οf slοw multiply charged iοns with large, free radiοsensitizing metallic nanοparticles|', 'en', v_resume, v_resume_a, q'|Nanoparticules métalliques, ions multichargés, spectrometrie de masse à temps de vol, capture multi- électronique, fission asymétrique, pulvérisation, hadronthérapie, radiosensibilisants|', NULL, q'|Interaction d'ions multichargés lents avec des nanoparticules métalliques radiosensibilisantes|', q'|Metallic nanoparticles, multiply charged ions, time-of-flight mass spectrometry, multielectron capture, asymmetric fission, sputtering, radiosensitizers, hadrontherapy|'
